        this.setState({userInput: e});
      console.log(e)
    }
     render() {
        return(
            <div>
                <input type="text" 
                placeholder='What to you want to buy?'
       onChange={(e)=>{this.onChangeEvent(e.target.value)}} 
       value={this.state.userInput}/>
            </div>
        )
     }
}
